import { useSelector } from "rea"
import CartItem from "../components/CartItem";
import { Link } from "react-router-dom";
import { useEffect } from "react";

const Cart = () => {

  const { cart } = useSelector((state) => state);
  const [totalAmmount,setTotalAmount] = useState(0);

  useEffect( () => {
    setTotalAmount( cart.reduce((acc,curr) => acc+curr.price),0); // reduce function implementation.
  },[cart])
  return (
    <div>
      {
        cart.length > 0 ?
          (<div>
            <div>
              {
                cart.map((item, index) => {
                  return <CartItem key={item.id} item={item} />
                })
              }
            </div>

              <div>
                <div>Your Cart </div>
                <div>Summary</div>
                <p>
                  <span>Total Items : {cart.lenght}</span>
                </p>
                </div>
                <div>
                  <p>Total Amount : {totalAmmount}</p>
                  <div>
                    <button>
                      Check Out
                    </button>
                  </div>
                </div>
          </div>) :
          (
            <div>
              <h1>Cart Empty</h1>
              <Link to={"/"}>
                <button>
                  Shop Now
                </button>
              </Link>
            </div>
          )
      }
    </div>
  );
};

export default Cart;
