import { useSelector } from "rea"
import CartItem from "../components/CartItem";
import { Link } from "react-router-dom";

const Cart = () => {

  const { cart } = useSelector((state) => state);
  const [totalAmmount,setTotalAmount] = use
  return (
    <div>
      {
        cart.length > 0 ?
          (<div>
            <div>
              {
                cart.map((item, index) => {
                  return <CartItem key={item.id} item={item} />
                })
              }
            </div>

              <div>
                <div>Your Cart </div>
                <div>Summary</div>
                <p>
                  <span>Total Items : {cart.lenght}</span>
                </p>
                </div>

          </div>) :
          (
            <div>
              <h1>Cart Empty</h1>
              <Link to={"/"}>
                <button>
                  Shop Now
                </button>
              </Link>
            </div>
          )
      }
    </div>
  );
};

export default Cart;
