import { useSelector } from "react-redux";

const Product = ({post}) => {

  const  {cart} = useSelector( (state) => state);
  return (
    <div>
      <div>
        <div>
          <p>{post.title}</p>
        </div>
        
        <div>
          <p>{post.description}</p>
        </div>
        
        <div>
          <img src={post.image}/>
        </div>
        
        <div>
          <p>{post.price}</p>
        </div>

          {
            cart.some((p) => p.id == post.id) ?
            (
              <button>
                Add to Cart
              </button>
            ):
            (
              <button>
                
              </button>
            )
          }

      </div>
    </div>
  );
};

export default Product;
